'use strict';

const dogAPI = require("./lib/dog")
const catAPI = require("./lib/cat")
const foxAPI = require("./lib/fox")
const shibaAPI = require("./lib/shiba")
const birdAPI = require("./lib/bird")

/**
 * @returns {String} The link to the picture of the random dog image
 */
const getRandomDogImage = async () => {
  const res = await dogAPI()
  return res.url
}

/**
 * @returns {String} The link to the picture of the random cat image
 */
const getRandomCatImage = async () => {
  const res = await catAPI()
  return res[0]
}

/**
 * @returns {String} The link to the picture of the random fox image
 */
const getRandomFoxImage = async () => {
  const res = await foxAPI()
  return res.image
}

/**
 * @returns {String} The link to the picture of the random shiba inu image
 */
const getRandomShibaInuImage = async () => {
  const res = await shibaAPI()
  return res[0]
}

/**
 * @returns {String} The link to the picture of the random bird image
 */
const getRandomBirdImage = async () => {
  const res = await birdAPI()
  return res[0]
}

module.exports = {
  getRandomDogImage,
  getRandomCatImage,
  getRandomFoxImage,
  getRandomShibaInuImage,
  getRandomBirdImage
}